mapper.select:
SELECT sn.*,lemp.emp_number as emp_number,lemp.emp_mobile as emp_mobile,lemp.emp_name as emp_name,lemp.emp_cover as emp_cover from lx_card_emp sn LEFT JOIN lx_emp lemp ON lemp.emp_id = sn.emp_id WHERE 1=1
AND sn.emp_id = #{emp_id}
AND sn.is_use = #{is_use}
AND lemp.emp_name like CONCAT('%',#{keyWords},'%')
AND lemp.emp_mobile like CONCAT('%',#{emp_mobile},'%')
AND lemp.emp_number like CONCAT('%',#{emp_number},'%')
ORDER BY sn.lx_card_emp_start_time DESC limit #{index},#{size}

SELECT count(sn.emp_id) FROM lx_card_emp sn LEFT JOIN lx_emp lemp ON lemp.emp_id = sn.emp_id WHERE 1=1
AND sn.emp_id = #{emp_id}
AND sn.is_use = #{is_use}
AND lemp.emp_name like CONCAT('%',#{keyWords},'%')
AND lemp.emp_mobile like CONCAT('%',#{emp_mobile},'%')
AND lemp.emp_number like CONCAT('%',#{emp_number},'%')

mapper.update:
UPDATE lx_card_emp SET lx_card_emp_year=#{lx_card_emp_year},
lx_card_emp_end_time=#{lx_card_emp_end_time} ,
 is_use=#{is_use}
 WHERE emp_id=#{emp_id}
