mapper.select:
SELECT gc.*, le.emp_name as nick_name, le.emp_cover as cover, gt.comment_emp_id as f_emp_id, lp.emp_name as f_nick_name, lgoods.goods_name as goods_name FROM goods_comment gc LEFT JOIN lx_emp le ON gc.comment_emp_id=le.emp_id LEFT JOIN goods_comment gt ON gc.comment_fplid=gt.comment_id LEFT JOIN lx_emp lp ON gt.comment_emp_id=lp.emp_id LEFT JOIN paopao_goods lgoods ON lgoods.goods_id=gc.goods_id WHERE 1=1
and gc.goods_id=#{goods_id}
and gc.comment_emp_id=#{emp_id}
and gc.goods_emp_id=#{goods_emp_id}
and gc.is_del=#{is_del}
ORDER BY gc.dateline DESC limit #{index},#{size}

SELECT gc.*,le.emp_name as nick_name,le.emp_cover as cover,gt.comment_emp_id as f_emp_id, lp.emp_name as f_nick_name
FROM goods_comment gc
LEFT JOIN lx_emp le ON gc.comment_emp_id=le.emp_id
LEFT JOIN goods_comment gt ON gc.comment_fplid=gt.comment_id
LEFT JOIN lx_emp lp ON gt.comment_emp_id=lp.emp_id
WHERE  1=1

    and gc.goods_id=#{goods_id}



    and gc.goods_emp_id=#{goods_emp_id}


    and gc.is_del=#{is_del}

select sum(le.starNumber) from goods_comment le
WHERE 1=1

    AND le.goods_id = #{goods_id}


    AND le.goods_emp_id = #{goods_emp_id}

select count(le.comment_id) from goods_comment le
WHERE 1=1

    AND le.goods_id = #{goods_id}


    AND le.goods_emp_id = #{goods_emp_id}


    and le.is_del=#{is_del}
