mapper.select:
SELECT sn.*,le.emp_number as emp_number,le.emp_mobile as emp_mobile,le.emp_name as emp_name,le.emp_cover as emp_cover ,lbank.bank_emp_name as bank_emp_name ,lbank.bank_mobile as bank_mobile ,lbank.bank_kaihu_name as bank_kaihu_name ,lbank.bank_name as bank_name ,lbank.bank_card as bank_card from lx_bank_apply sn LEFT JOIN lx_emp le ON le.emp_id = sn.emp_id LEFT JOIN lx_bank lbank ON lbank.bank_id = sn.bank_id WHERE 1=1
AND sn.emp_id = #{emp_id}
AND sn.is_check = #{is_check}
AND le.emp_mobile = #{phoneNumber}
AND le.emp_name like CONCAT('%',#{keyWords},'%')
ORDER BY sn.dateline_apply DESC limit #{index},#{size}

SELECT count(sn.lx_bank_apply_id) FROM lx_bank_apply sn
LEFT  JOIN lx_emp le ON le.emp_id = sn.emp_id
WHERE 1=1

    AND sn.emp_id = #{emp_id}


    AND sn.is_check = #{is_check}


    AND le.emp_mobile = #{phoneNumber}


    AND le.emp_name like CONCAT('%',#{keyWords},'%')
