mapper.select:
SELECT lmanager.*, le.emp_name as emp_name, le.emp_cover as emp_cover, le.emp_mobile as emp_mobile, le.is_card_emp as is_card_emp, lclass.lx_class_name as lx_class_name, lclass.lx_class_cover as lx_class_cover, ltype.type_name as type_name FROM MANAGER_INFO lmanager LEFT JOIN lx_emp le ON lmanager.emp_id=le.emp_id LEFT JOIN lx_goods_type ltype ON lmanager.type_id=ltype.type_id LEFT JOIN lx_class lclass ON lclass.lx_class_id=lmanager.lx_class_id WHERE 1 = 1
AND (lmanager.company_name like CONCAT('%',#{cont},'%') OR lmanager.company_tel like CONCAT('%',#{cont},'%')OR lmanager.company_person like CONCAT('%',#{cont},'%') )
AND lmanager.is_card = #{is_card}
ORDER BY lmanager.dateline DESC limit #{index},#{size}

SELECT count(INFO_ID) FROM MANAGER_INFO WHERE 1 = 1
AND (company_name like CONCAT('%',#{cont},'%') OR company_tel like CONCAT('%',#{cont},'%')OR company_person like CONCAT('%',#{cont},'%') )
AND is_card = #{is_card}

mapper.update:
UPDATE MANAGER_INFO SET
is_check=#{is_check}
WHERE INFO_ID=#{id}

UPDATE MANAGER_INFO SET
is_card=#{is_card}
WHERE INFO_ID=#{id}
