SELECT ord.* ,le.emp_name as empName, le.emp_cover as empCover,le.EMP_MOBILE as empMobile ,lg.goods_name as goodsTitle,lg.goods_cover as goodsCover,lg.SELL_PRICE as goodsPrice from PAOPAO_SHOPPING_ORDER ord LEFT JOIN lx_emp le ON ord.seller_emp_id=le.emp_id LEFT JOIN PAOPAO_GOODS lg ON ord.goods_id=lg.goods_id WHERE 1=1 AND ord.EMP_ID = #{emp_id} AND ord.STATUS !='4' AND ord.status != '1'
AND ord.status=#{status}
ORDER BY ord.create_time DESC limit #{index},#{size}

SELECT COUNT(*) from PAOPAO_SHOPPING_ORDER WHERE 1='1'

    AND SELLER_EMP_ID = #{emp_id}


    AND PAY_TIME >= #{start}


    AND PAY_TIME <= #{end}

SELECT sum(PAYABLE_AMOUNT) from PAOPAO_SHOPPING_ORDER WHERE 1='1'

    AND SELLER_EMP_ID = #{emp_id}


    AND PAY_TIME >= #{start}


    AND PAY_TIME <= #{end}

AND PAY_STATUS = '1'

    SELECT ord.* ,le.emp_name as empName, le.emp_cover as empCover,le.EMP_MOBILE as empMobile ,lg.goods_name as goodsTitle,lg.goods_cover as goodsCover,lg.SELL_PRICE as goodsPrice from PAOPAO_SHOPPING_ORDER ord
    LEFT JOIN lx_emp le ON ord.EMP_ID=le.emp_id
    LEFT JOIN PAOPAO_GOODS lg ON ord.goods_id=lg.goods_id

    WHERE 1=1 AND ord.seller_emp_id = #{emp_id}

        AND ord.status=#{status}

    ORDER BY ord.create_time DESC

limit #{index},#{size}

SELECT * from PAOPAO_SHOPPING_ORDER
WHERE is_dxk_order = '1' AND create_time > #{startTime} AND create_time < #{endTime}

    AND emp_id = #{emp_id}


    AND seller_emp_id = #{seller_emp_id}
