mapper.select:
SELECT pg.*, ltype.type_name as type_name , lemp.emp_name as nick_name, lemp.emp_cover as emp_cover, lmg.manager_cover as managerCover, lmg.manager_admin as managerName, lph.mm_paihang_id as mm_paihang_id, lclass.lx_class_name as lx_class_name FROM PAOPAO_GOODS pg LEFT JOIN lx_goods_type ltype ON ltype.type_id = pg.goods_type_id LEFT JOIN lx_emp lemp ON lemp.emp_id = pg.goods_emp_id LEFT JOIN lx_manager lmg ON lmg.manager_id = pg.manager_id LEFT JOIN manager_info lmanager ON lmanager.emp_id = pg.goods_emp_id LEFT JOIN mm_paihang lph ON lph.goods_id = pg.goods_id LEFT JOIN lx_class lclass ON lclass.lx_class_id = pg.lx_class_id WHERE pg.goods_is_del='0'
AND pg.goods_count != '0'
AND pg.goods_type_id IN
#{item}
AND pg.goods_is_use=#{isUse}
AND pg.is_dxk=#{is_dxk}
AND (pg.goods_name like CONCAT('%',#{cont},'%') OR pg.GOODS_CONT like CONCAT('%',#{cont},'%') )
AND pg.goods_emp_id=#{empId}
AND pg.is_zhiying=#{is_zhiying}
AND pg.goods_position=#{goods_position}
AND pg.manager_id=#{manager_id}
AND lmanager.lat_company > (#{lat_company}-1) AND (#{lat_company} + 1) > lmanager.lat_company
AND lmanager.lng_company > (#{lng_company}-1) AND (#{lng_company} + 1) > lmanager.lng_company
order by ACOS(SIN((#{lat_company} * 3.1415) / 180 ) *SIN((lmanager.lat_company * 3.1415) / 180 ) +COS((#{lat_company} * 3.1415) / 180 ) * COS((lmanager.lat_company * 3.1415) / 180 ) *COS((#{lng_company}* 3.1415) / 180 - (lmanager.lng_company * 3.1415) / 180 ) ) * 6380 asc
ORDER BY pg.up_time DESC
ORDER BY pg.goods_count_sale DESC
ORDER BY pg.top_number DESC
ORDER BY pg.dianpu_number DESC
limit #{index},#{size}

SELECT count(pg.GOODS_ID)
FROM PAOPAO_GOODS pg
LEFT JOIN lx_goods_type ltype ON ltype.type_id = pg.goods_type_id
LEFT JOIN lx_emp lemp ON lemp.emp_id = pg.goods_emp_id
LEFT JOIN lx_manager lmg ON lmg.manager_id = pg.manager_id
LEFT JOIN manager_info lmanager ON lmanager.emp_id = pg.goods_emp_id
WHERE  pg.goods_is_del='0'

    AND pg.goods_count != '0'


    AND pg.goods_type_id IN

        #{item}



    AND pg.goods_is_use=#{isUse}


    AND pg.is_dxk=#{is_dxk}


    AND (pg.goods_name like CONCAT('%',#{cont},'%') OR pg.GOODS_CONT like CONCAT('%',#{cont},'%') )


    AND pg.goods_emp_id=#{empId}



    AND pg.is_zhiying=#{is_zhiying}



    AND pg.goods_position=#{goods_position}



    AND pg.manager_id=#{manager_id}



    AND lmanager.lat_company > (#{lat_company}-1) AND   (#{lat_company} + 1) > lmanager.lat_company


    AND lmanager.lng_company > (#{lng_company}-1) AND   (#{lng_company} + 1) > lmanager.lng_company

mapper.update:
UPDATE PAOPAO_GOODS SET
top_number=#{top_number},
goods_position=#{goods_position}
WHERE GOODS_ID=#{id}

mapper.select:
SELECT pg.*,ltype.type_name as type_name
FROM PAOPAO_GOODS pg
LEFT JOIN lx_goods_type ltype ON ltype.type_id = pg.goods_type_id
WHERE  pg.goods_is_del='0' AND pg.goods_is_use = '0'

    AND pg.goods_type_id=#{typeId}


    AND (pg.goods_name like CONCAT('%',#{cont},'%') OR pg.GOODS_CONT like CONCAT('%',#{cont},'%') )


    AND pg.goods_emp_id=#{empId}


    AND pg.manager_id=#{manager_id}

ORDER BY pg.up_time DESC

SELECT pg.*, ltype.type_name as type_name , lemp.emp_name as nick_name, lemp.emp_cover as emp_cover, lmg.manager_cover as managerCover, lmg.manager_admin as managerName, lclass.lx_class_name as lx_class_name FROM PAOPAO_GOODS pg LEFT JOIN lx_goods_type ltype ON ltype.type_id = pg.goods_type_id LEFT JOIN lx_emp lemp ON lemp.emp_id = pg.goods_emp_id LEFT JOIN lx_manager lmg ON lmg.manager_id = pg.manager_id LEFT JOIN manager_info lmanager ON lmanager.emp_id = pg.goods_emp_id LEFT JOIN lx_class lclass ON lclass.lx_class_id = pg.lx_class_id WHERE pg.goods_is_del='0' AND pg.goods_is_use = '0' AND pg.goods_position = '1'
AND (pg.goods_name like CONCAT('%',#{cont},'%') OR pg.GOODS_CONT like CONCAT('%',#{cont},'%') )
ORDER by pg.top_number DESC limit #{index},#{size}
